import {createApp} from 'vue'
import Vuex from 'vuex'
Vue.use(Vuex)
//创建状态
const state = {
    sum:0
}
//创建actions
const actions = {
    add1:function(){
        console.log("actions.add1 has been used")
    }
}
//创建mutataions
const mutations = {

}

//创建store
export default new Vuex.Store({

state,
actions,
mutations
})
