import { createStore } from "vuex";
import Vue from 'vue'
import Vuex from 'vuex'
Vue.use(Vuex)
//创建状态
const state = {
    sum:0
}
//创建actions
const actions = {
    add1:function(){
        console.log("actions.add1 has been used")
    }
}
//创建mutataions
const mutations = {

}

//创建store
const store = createStore({

state,
actions,
mutations
})
//暴露出去
export default store
